def is_start_of_packet_marker(slice)
  slice.chars.uniq.length === slice.length    
end

class Subroutine

  def initialize(buffer, message_length)
    @message_length = message_length
    @buffer = buffer
  end

  def seek()
    start = 0
    slice = @buffer[start, @message_length]

    while !self.is_start_of_packet_marker(slice)
      slice = @buffer[start, @message_length]
      start += 1
    end

    [start + @message_length - 1, slice]
  end

end

input = ""

subroutine1 = Subroutine::new(input, 4)
subroutine2 = Subroutine::new(input, 14)
puts subroutine1.seek()
puts subroutine2.seek()

